              <button @click="changeGetMode()">已有账号，点击登录</button>
            </div>
          </div>
          <div class="login-register-input-contain" ref="inputContain">
            <!--      登录内容容器      -->
            <div v-show="loginMode === 1" class="login-register-input-div">
              <p>Login</p>
              <input type="text" placeholder="请输入账号" ref="login-account-input">
              <div ref="login-account-input-warn"></div>
              <input type="password" placeholder="请输入密码" ref="login-password-input">
              <div ref="login-password-input-warn"></div>
              <button @click="login()">登录</button>
            </div>

            <!--      注册内容容器      -->
            <div v-show="loginMode === 0" class="login-register-input-div">
              <p>Register</p>
              <input type="text" placeholder="请输入您的账号" ref="register-account-input">
              <div ref="register-account-input-warn-text"></div>
              <input type="text" placeholder="请输入您的账户名" ref="register-name-input">
              <div ref="register-name-input-warn-text"></div>
              <input type="password" placeholder="请输入您的密码" ref="register-password-input">
              <div ref="register-password-input-warn-text"></div>
              <input type="password" placeholder="请确认您的密码" ref="register-confirm-password-input">
              <div ref="register-confirm-password-input-warn-text"></div>
              <button @click="register">创建账户</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>

export default {
  name: 'LoginRegister',

  data() {
    return {
      loginMode: 1,
      loginWarnCode: 0,
      loginWarnText: [
        "",
        "账号或密码输入错误，请检查"
      ],

      registerWarnCode: 0,
      registerWarnText: [
          "",
          "账号重复，请重新输入账号",
          "账号需要由8位或8位以上的数字或字母组成",
          "账户名不得为空",
          "密码需要由8位或8位以上的数字或字母组成",
          "两次输入的密码不一致，请检查",
      ],

      Regex: /^(?=.*[a-zA-Z\d]).{8,}$/
    }
  },

  computed: {
  },


  watch: {
    loginWarnCode: {
      handler: function (newValue) {
        this.$refs["login-password-input-warn"].innerText = this.loginWarnText[newValue]
      }
    },
    registerWarnCode: {
      handler() {

      }
    }
  },

  methods: {
    //登录-注册输入框转换位置
    changeGetMode: function() {
      if(this.loginMode === 1) {
        this.loginMode = 0
        this.$refs.inputContain.style.left = "calc(50% - 300px)"
        this.$refs.inputContain.style.height = '500px'
      }
      else {
        this.loginMode = 1
        this.$refs.inputContain.style.left = "calc(50%)";
        this.$refs.inputContain.style.height = '400px'
      }
    },

    //登录操作
    login: function () {
      this.$axios.post("home/login", {
        account: this.$refs["login-account-input"].value,
        password: this.$refs["login-password-input"].value
      }).then((response) => {
        //成功登录跳转
        if (response.code === '0') {
          localStorage.setItem("id", response.data.id)
          localStorage.setItem("account", response.data.account)
          localStorage.setItem("name", response.data.name)
          localStorage.setItem("password", response.data.password)
          this.$router.push('console/PersonBlogView/PersonBlogDetailView')
        }
        //登录失败跳转
        else if (response.code === '123') {
          this.loginWarnCode = 1
          this.$message({
            type: 'error',
            message: "登录失败，请检查"
          })
        }
      }, () => {

      })

    },

    register: function () {
      let account = this.$refs["register-account-input"].value
      let name = this.$refs["register-name-input"].value
      let password = this.$refs["register-password-input"].value
      let confirmPassword = this.$refs["register-confirm-password-input"].value
      //8位或8位以上的数字或密码组成

      if (this.Regex.test(account)) {
        this.$refs["register-account-input-warn-text"].innerText = this.registerWarnText[0]
      }
      else {
        this.$refs["register-account-input-warn-text"].innerText = this.registerWarnText[2]
      }

      if (this.$refs["register-name-input"].value === "") {
        this.$refs["register-name-input-warn-text"].innerText = this.registerWarnText[3]
      }
      else {
        this.$refs["register-name-input-warn-text"].innerText = this.registerWarnText[0]
      }

      if (this.Regex.test(password)) {
        this.$refs["register-password-input-warn-text"].innerText = this.registerWarnText[0]
      }
      else {
        this.$refs["register-password-input-warn-text"].innerText = this.registerWarnText[4]
      }

      if (password === confirmPassword) {
        this.$refs["register-confirm-password-input-warn-text"].innerText = this.registerWarnText[0]
      }
      else {
        this.$refs["register-confirm-password-input-warn-text"].innerText = this.registerWarnText[5]
      }

      let accountRes = this.Regex.test(account)
      let nameRes = this.$refs["register-name-input"].value !== ""
      let passwordRes = this.Regex.test(password)

      if (accountRes && nameRes && passwordRes && (password === confirmPassword)) {
        this.$axios.post("home/register", {
          account: account,
          name: name,
          password: password
        }, {
          headers: {
            "Content-Type": "application/json"
          }
        })
            .then((response) => {
                  if (response.code === '999') {
                    this.$refs["register-account-input-warn-text"].innerText = this.registerWarnText[1]
                    this.$message({
                      type: 'error',
                      message: "该账号已被注册"
                    })
                  }
                  else {
                    this.$refs["register-account-input-warn-text"].innerText = this.registerWarnText[0]
                    this.$message({
                      type: 'success',
                      message: "注册成功"
                    })
                    setTimeout(() => {
                      location.reload()
                    }, 1000)
                  }
            }
            ,(error) => {
                  console.log(error)
                })
      }

    },

  },

}

</script>

